mapper.select:
SELECT


FROM cms_article a


WHERE a.id = #{id}

SELECT



FROM cms_article a






			a.del_flag in ('0','3')



				a.del_flag = #{delFlag}




		AND a.acquisition_source = #{acquisitionSource}


		AND a.title LIKE
		'%'||#{title}||'%'
		'%'+#{title}+'%'
		CONCAT('%', #{title}, '%')



			AND a.posid=''



				AND a.posid LIKE
				'%'||#{posid}||'%'
				'%'+#{posid}+'%'
				CONCAT('%', #{posid}, '%')




		AND (a.category_id = #{category.id}

			or c.parent_ids like
			'%'||#{category.id}||'%'
			'%,'+#{category.id}+',%'
			CONCAT('%,', #{category.id}, ',%')

		)


		AND a.image = #{image}



		AND a.create_by in (#{createBy.id})



		AND a.create_date >= #{beginDate} AND a.create_date <=
		#{endDate}


		AND a.id=#{id}


		AND a.is_statis=#{isStatis}


		AND a.is_mobile_statis=#{isMobileStatis}





		ORDER BY ${page.orderBy}


		ORDER BY a.create_date desc

mapper.update:
UPDATE cms_article SET category_id = #{category.id}, title = #{title}, link = #{link}, color = #{color}, image = #{image}, shorttile=#{shorttile}, keywords = #{keywords}, description = #{description}, weight = #{weight}, weight_date = #{weightDate}, hits = #{hits}, posid = #{posid}, custom_content_view = #{customContentView}, view_config = #{viewConfig}, create_date = #{createDate}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, del_flag = #{delFlag}, managerid=#{managerid}, acquisition_source=#{acquisitionSource},
timing=#{timing},
istime=#{istime}, msgid=#{msgsource.sid}, static_url=#{staticUrl}, is_statis=#{isStatis}, is_mobile_statis = #{isMobileStatis}
WHERE id = #{id}

UPDATE cms_article SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

