mapper.select:
select max(c.id) AS "id", max(c.name) AS "name", max(cp.id) AS "parent.id", max(cp.name) AS "parent.name", count(*) AS "cnt", sum(a.hits) AS "hits", max(a.update_date) as "updateDate", max(o.id) AS "office.id", max(o.name) AS "office.name", a.is_statis as isStatis, a.is_mobile_statis as "isMobileStatis", a.static_url as staticUrl FROM cms_article a RIGHT JOIN cms_category c ON c.id = a.category_id JOIN cms_category cp ON c.parent_id = cp.id JOIN sys_office o ON o.id = c.office_id
a.del_flag = #{DEL_FLAG_NORMAL} AND c.site_id = #{site.id}
AND (c.office_id = #{office.id} OR o.parent_ids like '%'||#{office.id}||'%' '%'+#{office.id}+'%'
CONCAT('%', #{office.id}, '%') )
AND a.update_date >= #{beginDate}
AND a.update_date <= #{endDate}
AND (c.id = #{id} OR c.parent_ids LIKE '%'||#{id}||'%' '%'+#{id}+'%'
CONCAT('%', #{id}, '%') )
group by cp.sort, cp.id, c.sort, c.id order by cp.sort, cp.id, c.sort, c.id

SELECT a.id AS "id", a.category_id AS "category.id", a.title AS "title", a.link AS "link", a.color AS "color", a.image AS "image", a.keywords AS "keywords", a.description AS "description", a.weight AS "weight", a.weight_date AS "weightDate", a.hits AS "hits", a.posid AS "posid", a.custom_content_view AS "customContentView", a.view_config AS "viewConfig", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag", c.name AS "category.name", u.name AS "user.name", a.managerid AS "managerid", a.acquisition_source AS acquisitionSource, a.timing AS timing, a.istime AS istime, a.msgid AS "msgid", m.sid AS "msgsource.sid", m.name AS "msgsource.name", m.url AS "msgsource.url", tp.description AS "posName", d.content AS "articleData.content", a.is_statis as isStatis, a.static_url as staticUrl, a.is_mobile_statis as "isMobileStatis" FROM cms_article a
LEFT join t_position tp ON tp.posid = a.posid
a.del_flag = #{delFlag}
and a.posid IS NOT null and a.posid !=''
AND a.acquisition_source = #{acquisitionSource}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND (a.category_id = #{category.id}
or c.parent_ids like '%'||#{category.id}||'%' '%,'+#{category.id}+',%' CONCAT('%,', #{category.id}, ',%')
)
AND a.posid = #{posid} or a.posid LIKE CONCAT('%,',#{posid}) or a.posid LIKE CONCAT(#{posid},',%') or a.posid LIKE CONCAT('%,', #{posid}, ',%')
AND a.image = #{image}
AND a.create_by in (#{createBy.id})
AND a.update_date >= #{beginDate} AND a.update_date <= #{endDate}
ORDER BY ${page.orderBy}
ORDER BY a.weight_date desc, a.number desc

SELECT a.id AS "id", a.category_id AS "category.id", a.title AS "title", a.link AS "link", a.color AS "color", a.image AS "image", a.keywords AS "keywords", a.description AS "description", a.weight AS "weight", a.weight_date AS "weightDate", a.hits AS "hits", a.posid AS "posid", a.custom_content_view AS "customContentView", a.view_config AS "viewConfig", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag", c.name AS "category.name", u.name AS "user.name", a.managerid AS "managerid", a.acquisition_source AS acquisitionSource, a.timing AS timing, a.istime AS istime, a.msgid AS "msgid", m.name AS "msgsource.name", m.sid AS "msgsource.sid", m.url AS "msgsource.url", tp.description AS "posName", d.content AS "articleData.content", a.is_statis as isStatis, a.static_url as staticUrl, a.is_mobile_statis as "isMobileStatis" FROM cms_article a
join t_position tp ON tp.posid = a.posid
a.del_flag = #{delFlag}
and a.posid IS NOT null and a.posid !=''
AND a.acquisition_source = #{acquisitionSource}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND (a.category_id = #{category.id}
or c.parent_ids like '%'||#{category.id}||'%' '%,'+#{category.id}+',%' CONCAT('%,', #{category.id}, ',%')
)
AND a.posid = #{posid} or a.posid LIKE CONCAT('%,',#{posid}) or a.posid LIKE CONCAT(#{posid},',%') or a.posid LIKE CONCAT('%,', #{posid}, ',%')
AND a.image = #{image}
AND a.create_by in (#{createBy.id})
AND a.update_date >= #{beginDate} AND a.update_date <= #{endDate}
ORDER BY ${page.orderBy}
ORDER BY a.weight_date desc, a.number desc

